'use client';

import React, { useEffect, useState, useRef } from 'react';
import { ChevronLeftIcon, ChevronRightIcon } from '@heroicons/react/24/solid';
import Loader from '../Loader';
import Image from "next/image";
const FeaturedCategories = () => {
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const sliderRef = useRef(null);
  const cardRef = useRef(null); // Reference to measure card width

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await fetch('/api/category'); // Adjust this URL to your API endpoint
        if (!response.ok) {
          throw new Error('Failed to fetch categories data');
        }
        const categoriesData = await response.json();

        // Filter categories to include only those with status "Top"
        const topCategories = categoriesData.filter(
          (category) => category.category_status === 'Top'
        );

        // Duplicate the array to enable infinite scrolling
        setCategories([...topCategories, ...topCategories]);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching categories:', error);
        setError(error.message);
        setLoading(false);
      }
    };

    fetchCategories();
  }, []);

  const scrollSlider = (direction) => {
    if (sliderRef.current && cardRef.current) {
      const cardWidth = cardRef.current.offsetWidth + 48; // Card width + gap
      sliderRef.current.scrollLeft += direction === 'left' ? -cardWidth : cardWidth;
    }
  };

  const handleScroll = () => {
    if (sliderRef.current) {
      const { scrollLeft, scrollWidth, clientWidth } = sliderRef.current;

      // If the user scrolls to the end, reset to the start (duplicate array gives illusion of infinite scrolling)
      if (scrollLeft >= scrollWidth / 2) {
        sliderRef.current.scrollLeft = 0;
      } else if (scrollLeft === 0) {
        sliderRef.current.scrollLeft = scrollWidth / 2;
      }
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-100">
        <Loader/>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-red-100 text-red-700">{`Error: ${error}`}</div>
    );
  }

  return (
    <div className="py-12 bg-gray-50 relative">
      <h2 className="text-3xl font-bold text-center mb-8">Top Categories</h2>

      <div className="container mx-auto px-4 sm:px-6 lg:px-8 relative">
        {/* Left Arrow */}
        <button
          onClick={() => scrollSlider('left')}
          className="absolute left-0 top-1/2 transform -translate-y-1/2 bg-white border border-gray-300 rounded-full shadow-lg p-2 z-10 hover:bg-gray-100"
          aria-label="Scroll Left"
        >
          <ChevronLeftIcon className="h-6 w-6 text-gray-500" />
        </button>

        {/* Right Arrow */}
        <button
          onClick={() => scrollSlider('right')}
          className="absolute right-0 top-1/2 transform -translate-y-1/2 bg-white border border-gray-300 rounded-full shadow-lg p-2 z-10 hover:bg-gray-100"
          aria-label="Scroll Right"
        >
          <ChevronRightIcon className="h-6 w-6 text-gray-500" />
        </button>

        {/* Categories Slider */}
        <div
          ref={sliderRef}
          onScroll={handleScroll}
          className="flex justify-start items-center space-x-12 overflow-x-hidden scroll-smooth pb-4 p-4 whitespace-nowrap"
        >
          {categories.map((category, index) => (
            <a
              key={index}
              ref={index === 0 ? cardRef : null} // Reference the first card to get the card width
              href={`/category/${category.web_slug}`}
              className="inline-block duration-300 ease-in-out text-center transform hover:scale-105"
            >
              <div className="w-40 h-40 sm:w-40 sm:h-40 mx-auto overflow-hidden rounded-full border-4 border-gray-300">
                <Image
                 width={1000}
                  height={1000}
                  placeholder="data:image/jpeg;base64,"
          
                  src={`https://m3xtrader.com/coupon/uploads/${category.category_image}`} // Replace with the correct path to the images
                  alt={category.cat_title}
                  className="w-full h-full object-contain"
                />
              </div>
              <h3 className="text-center text-md sm:text-md font-semibold mt-4">
                {category.category_name}
              </h3>
            </a>
          ))}
        </div>
      </div>
    </div>
  );
};

export default FeaturedCategories;
